  static ProjectData fromMap(Map<String, dynamic> map) {
    ProjectData projectData = new ProjectData();
    projectData.errorMsg = map['errorMsg'];
    projectData.errorCode = map['errorCode'];
    projectData.data = DataBean.fromMap(map['data']);
    return projectData;
  }

  static List<ProjectData> fromMapList(dynamic mapList) {
    List<ProjectData> list = new List(mapList.length);
    for (int i = 0; i < mapList.length; i++) {
      list[i] = fromMap(mapList[i]);
    }
    return list;
  }

